import ModuleFactory from './module-factory'

const lifeCircle = {
  init(cb) {
    console.log('init')
  },

  ready(cb) {
    $(() => {
      // 扫描绑定组件对象与 DOM 元素
      const context = ModuleFactory.init()

      typeof(cb) === 'function' && cb(context)
    })
  },

  destroy() {
    console.log('destroy')
  }
}

export default lifeCircle
