import Zepto from './zepto.js'
import { event, superEvent } from './event.js'
import BaseModule from './module-control.js'
import Circle from './circle.js'
import Extra from './extra.js'
import * as Utils from './utils.js'

function init() {
  // 挂载全局命名空间，暴露 Circle.ready 函数与事件模块，
  // SINA_NEWS.event 模块阉割 showEvents 方法
  window.SINA_NEWS = window.SINA_NEWS || { ready: Circle.ready, event }

  Extra.load()
}

init()

export {
  Zepto as default,
  Zepto as $,
  BaseModule,
  Utils,
  Zepto,
  Circle as Page,
  superEvent as Event
}
